package itaf.framework.business.platform.service;import itaf.framework.core.common.PageResult;import itaf.framework.core.common.QueryCriteria;import itaf.framework.domain.platform.SysCodeList;import java.util.List;/** * 该类定义访问编码列表数据的Service接口。 *  * @author   *  */public interface ISysCodeListService {	/**	 * 创建编码常量	 * 	 * @param codeTypeId	 *            属于哪个编码类型	 * @param tcCodeList	 *            编码常量信息	 * @return SysCodeList	 */	SysCodeList doCreate(SysCodeList tcCodeList);	/**	 * 根据编码常量Id删除	 * 	 * @param SysCodeList	 *            tcCodeList 编码常量	 */	void doDelete(SysCodeList tcCodeList);	/**	 * 根据编码常量Id进行查询	 * 	 * @param codeListId	 *            编码常量Id	 * @return SysCodeList	 */	SysCodeList getById(Long codeListId);	/**	 * 获取名称对应的值	 * 	 * @param codeType	 *            类型	 * @param name	 *            名称，同时查看中文名称和英文名称	 * @return 返回名称对应的值	 */	SysCodeList findByName(String type, String name);	/**	 * 编码名称类型查询	 * 	 * @param criteria	 *            sysCodeTypeId(can is null) and nameZh(can is null)	 * @return PageResult<SysCodeList>	 */	PageResult<SysCodeList> findByCriteria(QueryCriteria criteria);	/**	 * 查询全部	 * 	 * @return List<SysCodeList>	 */	List<SysCodeList> findAll();	/**	 * 更新SysCodeList	 * 	 * @param tcCodeList	 */	void doUpdate(SysCodeList tcCodeList);	List<SysCodeList> findByType(String type);}