package itaf.framework.persistence.platform.dao;import itaf.framework.domain.platform.SysCodeList;import itaf.framework.persistence.base.dao.IBaseDao;import java.util.List;/** *  * @author XINXIN *  */public interface ISysCodeListDao extends IBaseDao<SysCodeList> {	/**	 * 获取CodeValue对应的名称	 * 	 * @param type	 *            类型	 * @param codeValue	 *            值	 * @return 值对应的名称	 */	SysCodeList findByCodeValue(String type, String codeValue);	/**	 * 获取名称对应的值	 * 	 * @param codeType	 *            类型	 * @param name	 *            名称，同时查看中文名称和英文名称	 * @return 返回名称对应的值	 */	SysCodeList findByName(String type, String name);	/**	 * 根据类型查找列表	 * 	 * @param type	 *            类型	 * @return 数据列表	 */	List<SysCodeList> findByType(String type);	/**	 * 	 * @param currSortNo	 */	void updateSortNo4Create(int currSortNo, Long codeTypeId);	void updateSortNo4Delete(int currSortNo, Long codeTypeId);	/**	 * 	 * @param currSortNo	 */	void updateSortNo4Update(int oldSortNo, int currSortNo, Long codeTypeId);	int findSortNoById(Long codeListId);	Long findMaxSortNoByCodeType(String codeType);	List<SysCodeList> findByTypeId(Long codeTypeId);}